import React, { useState } from 'react';

const LoginForm = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailAvailable, setEmailAvailable] = useState(true);

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
        setEmailAvailable(true);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Check email availability via API call
        // Replace 'your-api-endpoint' with your actual server endpoint for email authorization
        fetch('your-api-endpoint', {
            method: 'POST',
            body: JSON.stringify({ email }),
            headers: {
                'Content-Type': 'application/json',
            },
        })
            .then((response) => response.json())
            .then((data) => {
                // Process the response data
                if (data.available) {
                    // Email is available, proceed with login
                    console.log('Login successful');
                } else {
                    // Email is not available
                    setEmailAvailable(false);
                }
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    return (
        <form className="login-form" onSubmit={handleSubmit}>
        <div className="field">
          <label className="label">Email</label>
          <div className="control">
            <input
              className={`input ${!emailAvailable ? 'is-danger' : ''}`}
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={handleEmailChange}
              required
            />
            {!emailAvailable && (
              <p className="help is-danger">This email is not available</p>
            )}
          </div>
        </div>
        <div className="field">
          <label className="label">Password</label>
          <div className="control">
            <input
              className="input"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
          </div>
        </div>
        <div className="field">
          <div className="control">
            <button type="submit" className="button is-primary">
              Login
            </button>
          </div>
        </div>
      </form>
    );
};

export default LoginForm;
