import React, { useState } from 'react';
import './LoginForm.scss';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailAvailable, setEmailAvailable] = useState(true);

  const handleEmailChange = (e) => {
    const sanitizedEmail = sanitizeInput(e.target.value);
    setEmail(sanitizedEmail);
    setEmailAvailable(true);
  };

  const handlePasswordChange = (e) => {
    const sanitizedPassword = sanitizeInput(e.target.value);
    setPassword(sanitizedPassword);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // ...existing code
  };

  const sanitizeInput = (input) => {
    // Encode input to prevent script execution
    const encodedInput = encodeHTML(input);
    return encodedInput;
  };

  const encodeHTML = (input) => {
    // Encode special characters to HTML entities
    const encodedText = input
      .replace(/&/g, '&amp;')
      .replace(/</g, '&lt;')
      .replace(/>/g, '&gt;')
      .replace(/"/g, '&quot;')
      .replace(/'/g, '&#039;');
    return encodedText;
  };

  return (
    <form className="login-form box" onSubmit={handleSubmit}>
      <div className="field">
        <p className={`control has-icons-left has-icons-right ${!emailAvailable ? 'has-icons-right-danger' : ''}`}>
          <input
            className={`input ${!emailAvailable ? 'is-danger' : ''}`}
            type="email"
            placeholder="Email"
            value={email}
            onChange={handleEmailChange}
            required
          />
          <span className="icon is-small is-left">
            <i className="fas fa-envelope"></i>
          </span>
          {!emailAvailable && (
            <span className="icon is-small is-right">
              <i className="fas fa-exclamation-triangle"></i>
            </span>
          )}
          {emailAvailable && (
            <span className="icon is-small is-right">
              <i className="fas fa-check"></i>
            </span>
          )}
        </p>
        {!emailAvailable && (
          <p className="help is-danger">This email is not available</p>
        )}
      </div>
      <div className="field">
        <p className="control has-icons-left">
          <input
            className="input"
            type="password"
            placeholder="Password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
          <span className="icon is-small is-left">
            <i className="fas fa-lock"></i>
          </span>
        </p>
      </div>
      <div className="field">
        <p className="control">
          <button type="submit" className="button is-success">
            Login
          </button>
        </p>
      </div>
    </form>
  );
};

export default LoginForm;
