import React, { useState } from 'react';
import Modal from 'react-modal';

const RegistrationForm = () => {
    const [fullName, setFullName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [dateOfBirth, setDateOfBirth] = useState('');
    const [gender, setGender] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [address, setAddress] = useState('');
    const [city, setCity] = useState('');
    const [state, setState] = useState('');
    const [country, setCountry] = useState('');
    const [postalCode, setPostalCode] = useState('');
    const [position, setPosition] = useState('');
    const [department, setDepartment] = useState('');
    const [employeeID, setEmployeeID] = useState('');
    const [hkID, sethkID] = useState('');
    const [agreedToTerms, setAgreedToTerms] = useState(false);
    const [isPrivacyPolicyOpen, setIsPrivacyPolicyOpen] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Check if the user has agreed to the terms
        if (!agreedToTerms) {
            alert('Please agree to the terms and conditions.');
            return;
        }

        // Set isRegistering to true to show loading state on the button
        setIsRegistering(true);

        // Perform further registration validation and submission
        // ...

        // Simulating an API call delay with setTimeout
        setTimeout(() => {
            // Reset form fields and state after successful registration
            setFullName('');
            setEmail('');
            setPassword('');
            setConfirmPassword('');
            setDateOfBirth('');
            setGender('');
            setPhoneNumber('');
            setAddress('');
            setCity('');
            setState('');
            setCountry('');
            setPostalCode('');
            setPosition('');
            setDepartment('');
            setEmployeeID('');
            sethkID('');
            setAgreedToTerms(false);

            // Set isRegistering back to false after the registration process is completed
            setIsRegistering(false);

            alert('Registration successful!');
        }, 2000); // 2 seconds delay for demonstration purposes
    };

    return (

        <form className="registration-form box" onSubmit={handleSubmit}>
            <div>
                <h1>Sign Up</h1>
            </div>
            <div className="columns">
                <div className="field column">
                    <label className="label">Employee ID</label>
                    <div className="control">
                        <input
                            className="input"
                            type="text"
                            placeholder="Enter your employee ID"
                            value={employeeID}
                            onChange={(e) => setEmployeeID(e.target.value)}
                            required
                        />
                    </div>
                </div>
                <div className="field column">
                    <label className="label">HKID</label>
                    <div className="control">
                        <input
                            className="input"
                            type="text"
                            placeholder="Enter your HKID ID"
                            value={hkID}
                            onChange={(e) => sethkID(e.target.value)}
                            required
                        />
                    </div>
                </div>
            </div>
            <div className='columns'>
                <div className="field column">
                    <label className="label">Full Name</label>
                    <div className="control">
                        <input
                            className="input"
                            type="text"
                            placeholder="Enter your full name"
                            value={fullName}
                            onChange={(e) => setFullName(e.target.value)}
                            required
                        />
                    </div>
                </div>
                <div className="field column">
                    <label className="label">Email</label>
                    <div className="control">
                        <input
                            className="input"
                            type="email"
                            placeholder="Enter your email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                </div>

            </div>
            <div className='columns'>
                <div className="field column">
                    <label className="label">Password</label>
                    <div className="control">
                        <input
                            className="input"
                            type="password"
                            placeholder="Enter your password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                </div>
                <div className="field column">
                    <label className="label">Confirm Password</label>
                    <div className="control">
                        <input
                            className="input"
                            type="password"
                            placeholder="Confirm your password"
                            value={confirmPassword}
