import React, { useState } from 'react';

const LoginForm = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [emailAvailable, setEmailAvailable] = useState(true);

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
        setEmailAvailable(true);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Check email availability via API call
        // Replace 'your-api-endpoint' with your actual server endpoint for email authorization
        fetch('your-api-endpoint', {
            method: 'POST',
            body: JSON.stringify({ email }),
            headers: {
                'Content-Type': 'application/json',
            },
        })
            .then((response) => response.json())
            .then((data) => {
                // Process the response data
                if (data.available) {
                    // Email is available, proceed with login
                    console.log('Login successful');
                } else {
                    // Email is not available
                    setEmailAvailable(false);
                }
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    return (
        <form className="login-form box" onSubmit={handleSubmit}>
            <div className="field">
                <p className={`control has-icons-left has-icons-right ${!emailAvailable ? 'has-icons-right-danger' : ''}`}>
                    <input
                        className={`input ${!emailAvailable ? 'is-danger' : ''}`}
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={handleEmailChange}
                        required
                    />
                    <span className="icon is-small is-left">
                        <i className="fas fa-envelope"></i>
                    </span>
                    {!emailAvailable && (
                        <span className="icon is-small is-right">
                            <i className="fas fa-exclamation-triangle"></i>
                        </span>
                    )}
                    {emailAvailable && (
                        <span className="icon is-small is-right">
                            <i className="fas fa-check"></i>
                        </span>
                    )}
                </p>
                {!emailAvailable && (
                    <p className="help is-danger">This email is not available</p>
                )}
            </div>
            <div className="field">
                <p className="control has-icons-left">
                    s
                    <input
                        className="input"
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={handlePasswordChange}
                        required
                    />
                    <span className="icon is-small is-left">
                        <i className="fas fa-lock"></i>
                    </span>
                </p>
            </div>
            <div className="field">
                <p className="control">
                    <button type="submit" className="button is-success">
                        Login
                    </button>
                </p>
            </div>
        </form>


    );
};

export default LoginForm;
